LazyScript.load('jquery', function (global) {
	function Browsing() {
		window.onload = e => {
			if (window.performance.navigation.type == 0) this.open();
			if (window.performance.navigation.type == 1) this.reload();
		}

		this.open = () => {
			var referrer = document.referrer, origin = window.location.origin;

			if (referrer.indexOf(origin) == 0) referrer = referrer.substr(origin.length);

			this.browsing({
				type: 1,
				referrer: referrer,
				page: window.location.pathname
			});
		}

		this.reload = () => {
			this.browsing({
				type: 2,
				page: window.location.pathname
			});
		}

		this.browsing = data => {
			$.post('/member/browsing', data);
		}
	}

	// new Browsing();
});